import React, { useState } from 'react'
import { View, Text, StyleSheet, TouchableOpacity, FlatList } from 'react-native'


const ColorsScreen = () => {
    const [colors, setColors] = useState(["blue", "red"])

    return (
        <View>
            <Text>Colors Screen</Text>
            <TouchableOpacity>
                <Text>Add random color</Text>
            </TouchableOpacity>
            <View style={style.colorsContainer}>
                <FlatList
                    data={colors}
                    keyExtractor={(color) => color}
                    renderItem={({ item }) => {
                        return <View style={[style.square, { backgroundColor: item }]}></View>
                    }}
                />
            </View>
        </View>
    );
}

const style = StyleSheet.create({
    colorsContainer: {
        paddingVertical: 30,
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center'
    },
    square: {
        height: 50,
        width: 50,
    }
})

export default ColorsScreen;
