import React, { useState } from 'react'
import { View, Text, StyleSheet, TouchableOpacity, FlatList } from 'react-native'


const ColorsScreen = () => {
    const [colors, setColors] = useState(["blue", "red"])

    return (
        <View>
            <Text>Colors Screen</Text>
            <TouchableOpacity>
                <Text>Add random color</Text>
            </TouchableOpacity>
            <View>
                <FlatList
                    data={colors}
                    keyExtractor={(color) => color}
                    renderItem={({ item }) => {
                        return <View style={{backgroundColor: item.color, ...style.square}}>
                            <Text>A</Text>

                        </View>
                    }}
                />
            </View>
        </View>
    );
}

const style = StyleSheet.create({
    square: {
        height: 20,
        width: 20
    }
})

export default ColorsScreen;
