import React, { useState } from 'react'
import { View, Text, StyleSheet, TouchableOpacity, FlatList } from 'react-native'


const ColorsScreen = () => {
    const [colors, setColors] = useState([])

    return (
        <View>
            <Text>Colors Screen: {colors.length}</Text>
            <TouchableOpacity
                style={style.button}
                onPress={() => setColors([...colors, `rgb(${Math.floor(Math.random() * 255)}, ${Math.floor(Math.random() * 255)}, ${Math.floor(Math.random() * 255)})`])}
            >
                <Text>Add random colors</Text>
            </TouchableOpacity>
            <View style={style.colorsContainer}>
                <FlatList
                    data={colors}
                    keyExtractor={(color) => color}
                    renderItem={({ item }) => {
                        return (
                            <View style={[style.square, {backgroundColor: item}]}>
                                <Text>{item}</Text>
                            </View>
                        )
                    }}
                />
            </View>
        </View>
    );
}

const style = StyleSheet.create({
    button: {
        backgroundColor: '#1e90ff',
        color: "white",
        padding: 30

    },
    colorsContainer: {
        paddingVertical: 30,
        height: 300,
        borderColor: 'green',
        borderWidth: 5
    },
    square: {
        height: 50,
        width: 50,
        marginHorizontal: 10,
        borderColor: "rgba(212, 212, 212, 0.5)",
        borderWidth: 0.5
    }
})

export default ColorsScreen;
