import React, { useState } from 'react'
import { View, Text, StyleSheet, TouchableOpacity, FlatList } from 'react-native'


const ColorsScreen = () => {
    const [colors, setColors] = useState(["blue", "red"])

    return (
        <View>
            <Text>Colors Screen</Text>
            <TouchableOpacity
            style={style.button}
                onPress={() => setColors([...colors, "#"+((1<<24)*Math.random()|0).toString(16)])}
            >
                <Text>Add random color</Text>
            </TouchableOpacity>
            <View style={style.colorsContainer}>
                <FlatList
                    data={colors}
                    keyExtractor={(color) => color}
                    renderItem={({ item }) => {
                        return <View style={[style.square, { backgroundColor: item }]}></View>
                    }}
                />
            </View>
        </View>
    );
}

const style = StyleSheet.create({
    button: {
        backgroundColor: "dodger blue",
        padding: 10

    },
    colorsContainer: {
        flex: 1,
        paddingVertical: 30,
    },
    square: {
        height: 50,
        width: 50,
        marginHorizontal: 10,
        borderColor: "rgba(212, 212, 212, 0.5)",
        borderWidth: 0.5
    }
})

export default ColorsScreen;
